myStack.push(2)
myStack.push(3)
console.log(myStack.pop()) // 3
console.log(myStack.getTop()) // 2
console.log(myStack.pop()) // 2
console.log(myStack.getTop()) // 1
console.log(myStack.isEmpty()) // false
console.log(myStack.pop()) // 1
console.log(myStack.isEmpty()) // true
